document.addEventListener("DOMContentLoaded",function(){var e=JSON.parse(localStorage.getItem("selectedPost"));if(e){var t=document.getElementById("postImage"),n=document.getElementById("postTitle"),m=document.getElementById("postBody"),o=document.getElementById("card_comments");t.src=e.imageUrl,n.textContent=e.title,m.textContent=e.body,e.comments&&e.comments.length>0&&e.comments.forEach(e=>{const t=document.createElement("div");t.className="comments_text";const n=`\n                    <p class="comment_user_email">\n                        ${e.user}: &nbsp${e.email}: \n                        <span class="comment_body">${e.body}</span>\n                        <hr class=comment_line"></hr>\n                    </p>\n                `;t.innerHTML=n,o.appendChild(t)})}});