document.addEventListener("DOMContentLoaded",function(){var e=JSON.parse(localStorage.getItem("selectedPost"));if(e){var t=document.getElementById("postImage"),n=document.getElementById("postTitle"),o=document.getElementById("postBody"),m=document.getElementById("card_comments");t.src=e.imageUrl,n.textContent=e.title,o.textContent=e.body,e.comments&&e.comments.length>0&&e.comments.forEach(e=>{const t=document.createElement("div");t.className="comments_text";const n=`\n                    <p><strong>${e.name}</strong> (${e.email})</p>\n                    <p class = "commentbody">${e.body}</p>\n                `;t.innerHTML=n,m.appendChild(t)})}});